create table "public"."weight_tickets" (
    "id" bigint generated by default as identity not null,
    "consignor_party_id" uuid not null,
    "carrier_party_id" uuid,
    "truck_license_plate" character varying,
    "reclamation" text,
    "note" text,
    "status" text not null,
    "created_at" timestamp with time zone not null,
    "updated_at" timestamp with time zone,
    "weighted_at" timestamp with time zone
);


alter table "public"."weight_tickets" enable row level security;

CREATE UNIQUE INDEX weight_tickets_pkey ON public.weight_tickets USING btree (id);

alter table "public"."weight_tickets" add constraint "weight_tickets_pkey" PRIMARY KEY using index "weight_tickets_pkey";

alter table "public"."weight_tickets" add constraint "weight_tickets_carrier_party_id_fkey" FOREIGN KEY (carrier_party_id) REFERENCES companies(id) not valid;

alter table "public"."weight_tickets" validate constraint "weight_tickets_carrier_party_id_fkey";

alter table "public"."weight_tickets" add constraint "weight_tickets_consignor_party_id_fkey" FOREIGN KEY (consignor_party_id) REFERENCES companies(id) not valid;

alter table "public"."weight_tickets" validate constraint "weight_tickets_consignor_party_id_fkey";

alter table "public"."weight_tickets" add constraint "weight_tickets_truck_license_plate_fkey" FOREIGN KEY (truck_license_plate) REFERENCES trucks(license_plate) not valid;

alter table "public"."weight_tickets" validate constraint "weight_tickets_truck_license_plate_fkey";

